mapper.select:
SELECT mid, email, mtel, pass, username, intro, mdate 
FROM member
WHERE

	email=#{email}


	mtel=#{mtel}
 

	username=#{username}

SELECT mid, email, mtel, pass, username, intro, mdate
FROM member
WHERE pass=#{pass} 

	and email=#{email}


	and mtel=#{mtel}
 

	and username=#{username}

mapper.insert:
INSERT INTO member
( mid, pass, username, mdate 

	,email


	,mtel


	,intro

) VALUES 
(member_mid_seq.nextval, #{pass}, #{username}, SYSDATE

	,#{email}
 

	,#{mtel}


	,#{intro}

)

mapper.update:
UPDATE member 
SET username=#{username}

	, pass=#{pass}
 

	,intro=#{intro}

WHERE 

	email=#{email}


	mtel=#{mtel}

mapper.select:
SELECT mid, email, mtel, pass, username, intro, mdate
FROM member
WHERE username=#{username} 

	and email=#{email}


	and mtel=#{mtel}
